func Test_apiFileReader(t *testing.T) {
	type args struct {
		file foundFile
	}
	tests := []struct {
		name    string
		args    args
		wantErr bool
	}{
		{
			name: "found file",
			args: args{file: foundFile{
				dataRuns: nil,
				fullPath: `test\testdata\dummyntfs`,
				fileSize: 0,
			}},
			wantErr: false,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			_, err := apiFileReader(tt.args.file)
			if (err != nil) != tt.wantErr {
				t.Errorf("apiFileReader() error = %v, wantErr %v", err, tt.wantErr)
				return
			}
		})
	}
}
